func TestCheckUserScope_HasScope(t *testing.T) {
	token := "eyJhbGciOiJSUzI1NiIsImtpZCI6ImxlZ2FjeS10b2tlbi1rZXkiLCJ0eXAiOiJKV1QifQ..ihgWURw0coxSOsiRWG-c_CWZpY9ykk5RB4dCDvGpHpx2d3Uid9dIKb-K9zOgkolGpKnu3_92oAYDYbVpqtJWmmVYBYXA0aZinUlV-8Xg6AnhWfB-NwrSvxqKXECzA6rZr6Uf6Wvg0UwgwjuFZfXpxQ6bsfntzhATOkbCcRuWfHGIq65Z8emj9pk4yNfn9AXBSf0I8MGmQCSQlDMgIywxGrvPwcfMlHRkXw65aN1o3vjCWA9Wabqy3RuoxXgL15yA1jp2Bp7EbPF4VL0bLUZBv1vt8rSA0S0ckPlUJhjCvZwdlHxgzvEuErrY1PuU63xiqQXEuJsU7etEtoN1uBOQVQ"

	hasScope, err := util.CheckUserScope(token, "cloud_controller.admin")

	if err != nil {
		t.Fatal(err.Error())
	}

	if !hasScope {
		t.Fatal("scope not present")
	}
}

